/*Class tinh UCLN vaf Fibonaci */public class UCLN {    /*    Ham tinh UCLN theo Euclid     */    public int UCLN(int x, int y){        while(x != y) {        if (x > y) x -= y;        else y -= x;        }        return x;    }    /*    Ham tinh fibonaci cua n (Ham De Quy)     */    public int Fibonaci(int n){            if(n>=0) {                switch (n) {                    case 0:                        return 0;                    case 1:                        return 1;                    default:                        return (Fibonaci(n - 1) + Fibonaci(n - 2));                }            }            else {                System.out.println("error input ");                return -1;            }        }     /*    Ham main     */    public static void main(String []args ) {        UCLN n = new UCLN();        System.out.println(n.UCLN(2,4) + "\n");        System.out.println(n.Fibonaci(4));    }}